import React, { Component } from 'react';
import DataTable from 'react-data-table-component';
import Api from '../../../services/sevices';
import {Redirect, Link } from 'react-router-dom';
import swal from 'sweetalert';
import Navbar from '../navbar';
import Menu from '../menu'; 
import "./post.css";
class category extends Component {
    constructor(props) {
        super(props);
        const token = sessionStorage.getItem('token');
        let login = true;
        if (token==null) {
            login = false;
        }
        this.state = {
            categorys :[],
            search:'',
            login
        }    
      }
    
    
    updatefilter(event){
        this.setState({
            search: event.target.value.substr(0,20)
        })
    }
    async componentDidMount() {
        await Api.get('category')
        .then(res => {
          const categorys = res.data['data'];
          this.setState({ categorys });
          console.log(this.state.categorys)
        })
      }

      publish (id,status){
        const data={publish: status}
        Api.put('category/publish/'+id,data)
        .then(res => {
          this.componentDidMount();         
        })
      }

      deletedata(id) {
        swal({
          title: "Anda yakin?",
          text: "Data yang anda pilih akan terhapus jika anda setuju tekan OK!",
          icon: "warning",
          buttons: true,
          dangerMode: true,
        })
        .then((willDelete) => {
          if (willDelete) {
            Api.delete('category/delete/'+id)
            .then(res => {
              swal("Data telah terhapus!", {
                icon: "success",
              });
               this.componentDidMount();              
            })   
            
           
          } else {
            swal("Data telah aman dan tidak jadi di hapus!");
          }
        });
        
      }

     

    render() {
        if (this.state.login === false ) {
            return <Redirect to="/admin/login" />
        }

        const filterid = this.state.categorys.filter(
          (datatemp) => {
              return datatemp.id_category.indexOf(this.state.search) !== -1;
          }
         )
        
        const filternama = this.state.categorys.filter(
          (datatemp) => {
              return datatemp.name_category.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1;
          }
      )
      

      const filterslug = this.state.categorys.filter(
        (datatemp) => {
            return datatemp.slug.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1;
        }
      )

      
      var datacategory =[];
      if(filterid.length > 0){
        datacategory = filterid; 
      }
      else if(filternama.length > 0){
           datacategory = filternama; 
      }
       else if(filterslug.length > 0)
      {
          datacategory = filterslug;
       }

       
       const priceSplitter = (number) => (number && number.toString().replace(/\B(?=(\d{3})+(?!\d))/g, '.'));
        const loopdata = datacategory.map(category=>{
            return({
                id : category.id_category,
                nama : category.name_category,
                slug : category.slug,
                action : [<a href="#" className="btn-edit"><i className="icon-edit"></i></a >,<a href="#" onClick={()=>this.deletedata(category.id_category)} className="btn-delete"><i className="icon-trash"></i></a>] })
        })
     
        const data = loopdata;
          const columns = [
            {
              name: 'Id',
              selector: 'id',
              sortable: true,
            },
            {
              name: 'Name Category',
              selector: 'nama',
              sortable: true,
            },
            {
                name: 'Slug',
                selector: 'slug',
                sortable: true,
              },
              {
                name: 'Action',
                selector: 'action',
                sortable: true,
              },
          ];

        
        
        return (
            <div>
            <Navbar/>
            <Menu />
            <div id="content">
            <div className="inner" >
                <div className="row">
                <div className="col-lg-6">

<div className="panel panel-default">
    <div className="panel-heading">
        Form Category
    </div>
    <div className="panel-body">
    <form className="form-horizontal popup-validation">
                    <div className="form-group">
                        <label className="control-label col-lg-4">Name</label>
                        <div className="col-lg-4">
                            <input name="nama" type="text" onChange={this.handleChange} className="validate[required] form-control" id="nama"/>
                        </div>
                    </div>
                    <div className="form-group">
                        <label className="control-label col-lg-4">Nomor Handphone</label>
                        <div className="col-lg-4">
                            <input name="nohp" type="number" onChange={this.handleChange} className="validate[required] form-control" id="nohp"/>
                        </div>
                    </div>
                    
                    <div className="form-group">
                        <label className="control-label col-lg-4">Alamat</label>
                        <div className="col-lg-4">
                            <textarea name="alamat" onChange={this.handleChange} className="validate[required] form-control" id="alamat"></textarea>
                        </div>
                    </div>

                    <div className="form-group">
                        <label className="control-label col-lg-4">Dana Dibutuhkan</label>
                        <div className=" col-lg-4">
                            <input name="biaya" onChange={this.handleChange} className="validate[required] form-control" type="number" id="biaya" />
                        </div>
                    </div>
                    <div className="form-group">
                        <label className="control-label col-lg-4">Rincian Kebutuhan</label>
                        <div className=" col-lg-4">
                            <input name="rincianbiaya" onChange={this.handleChange} className="validate[required] form-control" type="number" id="rincianbiaya" />
                        </div>
                    </div>
                    
                    <div className="form-group">
    <label className="control-label col-lg-4">Image Upload</label>
    <div className="col-lg-8">
        <div className="fileupload fileupload-new" data-provides="fileupload">
            <div className="fileupload-preview thumbnail" style={{width: "200px", height: "150px"}}></div>
            <div>
                <span className="btn btn-file btn-success"><span className="fileupload-new">Select image</span><span className="fileupload-exists">Change</span><input className="validate[required]" type="file" onChange={this.handleImageChange.bind(this)} /></span>
                <a href="#" className="btn btn-danger fileupload-exists" data-dismiss="fileupload">Remove</a>
            </div>
        </div>
    </div>
</div>
    
        <div style={{textAlign:"center"}} className="form-actions no-margin-bottom">
            <button onClick={this.handleSubmit} type="button" className="btn btn-primary ">Submit</button>
            <Link style={{marginLeft:"10px"}} className="btn btn-warning" to="/admin/penerima" >
            Kembali
            </Link>
        </div>
    </form>
       
    </div>
</div> 


</div>

