import React from 'react';
import {link, Link} from 'react-router-dom';

if(localStorage.getItem('role') !== 1){
    var hiddenmenu = {
        display : "none"
    }

}

function Menu(){
    return(
<div id="left">
    <div className="media user-media well-small">
        <a className="user-link" href="#">
            <img className="media-object img-thumbnail user-img" alt="User Picture" src={localStorage.getItem('foto')} />
        </a>
        <br />
        <div className="media-body">
            <h5 className="media-heading"> {localStorage.getItem('nama')}</h5>
        </div>
        <br />
    </div>

    <ul id="menu" className="collapse">

        
        <li className="panel">
            <Link to="/beranda" >
                <i className="icon-home"></i> Beranda               
            </Link>                   
        </li>
        <li style={hiddenmenu} className="panel">
            <Link to="/user" >
                <i className="icon-home"></i> Data User              
            </Link>                   
        </li>
    </ul>

</div>        
    )
}

export default Menu;
