import React, { Component } from 'react';
import Axios from 'axios';
import DataTable from 'react-data-table-component';

class chielduser extends Component {
    constructor(props) {
        super(props)
        this.state = {
            users :[]
        }    
      }
    

    async componentDidMount() {
       await Axios.get('http://localhost:8080/user')
          .then(res => {
            const users = res.data['data'];
            console.log(users);
            this.setState({ users });
          })
          
          

      }

      

    render() {
        const loopdata = this.state.users.map(user=>{
            if(user.level == 1){
               var Role = "Administrator";
            }else{
                var Role = "Editor";
            }
            return({
                nama : user.nama_user,
                email: user.email_user,
                role : Role,
                foto : <img width="100%" style={{padding :"20px"}} src={user.foto_user} />
            })
        })
        const data = loopdata;
          const columns = [
            {
              name: 'Nama',
              selector: 'nama',
              sortable: true,
            },
            {
              name: 'Email',
              selector: 'email',
              sortable: true,
            },
            {
                name: 'Role',
                selector: 'role',
                sortable: true,
              },
              {
                name: 'Foto',
                selector: 'foto',
                sortable: true,
              },
          ];

          
        
        return (
            <div id="content">
            <div className="inner" >
                <div className="row">
                    <div className="col-lg-12">

                    <div className="panel panel-default">
                        <div className="panel-heading">
                            Data User
                        </div>
                        <div className="panel-body">
                            <div className="table-responsive">
                            <div className="form-group input-group" style={{maxWidth:"300px", float:"right"}}>
                                            <input type="text" className="form-control"/>
                                            <span className="input-group-btn">
                                                <button className="btn btn-default" type="button">
                                                    <i className="icon-search"></i>
                                                </button>
                                            </span>
                                        </div>  
                            <DataTable
        columns={columns}
        data={data}
        pagination
        filter
      />

                            </div>
                           
                        </div>
                    </div> 


                    </div>
                </div>
                <hr />
            </div>
        </div>
        );
    }
}

export default chielduser;
