import React, { Component } from 'react';
import DataTable from 'react-data-table-component';
import Api from '../../services/sevices';

class chielduser extends Component {
    constructor(props) {
        super(props)
        this.state = {
            users :[],
            search:''
        }    
      }
    
    updatefilter(event){
        this.setState({
            search: event.target.value.substr(0,20)
        })
    }
    async componentDidMount() {
       await Api.get('user')
          .then(res => {
            const users = res.data['data'];
            console.log(users);
            this.setState({ users });
          })
          
              

      }

      

    render() {
        let filternama = this.state.users.filter(
            (datatemp) => {
                return datatemp.nama_user.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1;
            }
        )
        
        let filteremail = this.state.users.filter(
            (datatemp) => {
                return datatemp.email_user.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1;
            }
        )

        if(filternama.length > 0){
            var datauser = filternama; 
        }
        else
        {
            var datauser = filteremail
         }

        const loopdata = datauser.map(user=>{
            if(user.level == 1){
               var Role = "Administrator";
            }else{
                var Role = "Editor";
            }
            return({
                nama : user.nama_user,
                email: user.email_user,
                role : Role,
                foto : <img width="100%" style={{padding :"20px"}} src={user.foto_user} />
            })
        })

        
        const data = loopdata;
          const columns = [
            {
              name: 'Name',
              selector: 'nama',
              sortable: true,
            },
            {
              name: 'Email',
              selector: 'email',
              sortable: true,
            },
            {
                name: 'Role',
                selector: 'role',
                sortable: true,
              },
              {
                name: 'Foto',
                selector: 'foto',
                sortable: true,
              },
          ];

        
        
        return (
            <div id="content">
            <div className="inner" >
                <div className="row">
                    <div className="col-lg-12">

                    <div className="panel panel-default">
                        <div className="panel-heading">
                            Data User
                        </div>
                        <div className="panel-body">
                            <div className="table-responsive">
                            <div className="form-group input-group" style={{maxWidth:"300px", float:"right"}}>
                                            <input type="text" placeholder="Search" value={this.state.search} onChange={this.updatefilter.bind(this)} className="form-control"/>
                                        </div> 
                            <DataTable
        title="Arnold Movies"
        columns={columns}
        data={data}
        pagination
      />

                            </div>
                           
                        </div>
                    </div> 


                    </div>
                </div>
                <hr />
            </div>
        </div>
        );
    }

}

export default chielduser;
