import React, { Component } from 'react';
import Api from '../../../../services/sevices';

class slider extends Component {
    constructor(props) {
    super(props);
    this.State = {
        sliders : [],
    }
    
    }

    async componentDidMount() {
        await Api.get('slider/Home')
        .then(res => {
          const sliders = res.data['data'];
          this.setState({ sliders });
        })
      }

