import React, { Component } from 'react';
import DataTable from 'react-data-table-component';
import Api from '../../services/sevices';
import {link, Link} from 'react-router-dom';
import swal from 'sweetalert';
class chielduser extends Component {
    constructor(props) {
        super(props)
        this.state = {
            users :[],
            search:''
        }    
      }
    
    updatefilter(event){
        this.setState({
            search: event.target.value.substr(0,20)
        })
    }
    async componentDidMount() {
       await Api.get('user')
          .then(res => {
            const users = res.data['data'];
            this.setState({ users });
          })
          
              

      }

       deletedata(id) {

        swal("Kamu yakin?", {
            title: 'Kamu Yakin?',
            text: 'Data ini akan terhapus dari table',
            icon: 'warning',
            buttons: ["Cancel", "Yes!"],
          }).then(function(value) {
            if (value) {

               Api.delete('user/delete/'+id)
                   .then(res => {
                    swal(
                        'Berhasil!',
                        'Data terhapus.',
                        'success'
                      )         
                 
                   })
                
            }
        });
          
          
      }

     

    render() {
        let filternama = this.state.users.filter(
            (datatemp) => {
                return datatemp.nama_user.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1;
            }
        )
        
        let filteremail = this.state.users.filter(
            (datatemp) => {
                return datatemp.email_user.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1;
            }
        )

        if(filternama.length > 0){
            var datauser = filternama; 
        }
        else
        {
            var datauser = filteremail
         }

        const loopdata = datauser.map(user=>{
            if(user.level == 1){
               var Role = "Administrator";
            }else{
                var Role = "Editor";
            }
            return({
                nama : user.nama_user,
                email: user.email_user,
                role : Role,
                foto : <img width="100px" style={{margin :"auto", padding:"20px"}} src={user.foto_user} />,
                action : [<Link to={"user/edit/"+ user.id_user } className="btn-edit"><i className="icon-edit"></i></Link >,<Link  className="btn-delete"><i onClick={this.deletedata(user.id_user)} className="icon-trash"></i></Link>] })
        })
     
        const data = loopdata;
          const columns = [
            {
              name: 'Name',
              selector: 'nama',
              sortable: true,
            },
            {
              name: 'E-mail',
              selector: 'email',
              sortable: true,
            },
            {
                name: 'Role',
                selector: 'role',
                sortable: true,
              },
              {
                name: 'Foto',
                selector: 'foto',
                sortable: true,
              },
              {
                name: 'Action',
                selector: 'action',
                sortable: true,
              },
          ];

        
        
        return (
            <div id="content">
            <div className="inner" >
                <div className="row">
                    <div className="col-lg-12">

                    <div className="panel panel-default">
                        <div className="panel-heading">
                            Data User
                        </div>
                        <div className="panel-body">
                            <div className="table-responsive">
                            <Link className="btn-tambah" to="/user/add" >
                            <i className="icon-plus"></i>
                            </Link>
                            <div className="form-group input-group" style={{maxWidth:"300px", float:"right"}}>
                                            <input type="text" placeholder="Search" value={this.state.search} onChange={this.updatefilter.bind(this)} className="form-control"/>
                                        </div> 
                            <DataTable
        title="Arnold Movies"
        columns={columns}
        data={data}
        pagination
      />

                            </div>
                           
                        </div>
                    </div> 


                    </div>
                </div>
                <hr />
            </div>
        </div>
        );
    }

}

export default chielduser;
