import React, { Component } from 'react';

class login extends Component {
    constructor(props) {
        const token = localStorage.getItem('token');
        let login = false;
        if (token!=null) {
            login = true
        }
        super(props)
        this.state = {
            email:"",
            password:"",
            login
        }    
      }
    render() {
        return (
            <div>
                <div className="text-center">
        <img src="assets/img/logo.png" id="logoimg" alt=" Logo" />
    </div>
    <div className="tab-content">
        <div id="login" className="tab-pane active">
            <form className="form-signin popup-validation">
                <input type="email" name="email" placeholder="Your E-mail" className="form-control validate[required,custom[email]]" />
                <input type="password" name="password" placeholder="Password" className="form-control validate[required]" />
                <button className="btn text-muted text-center btn-danger" type="button">Sign in</button>
            </form>
        </div>
        <div id="forgot" className="tab-pane popup-validation">
            <form  className="form-signin">
                <input type="email" name="emailrecover"  required="required" placeholder="Your E-mail"  className="form-control validate[required,custom[email]]" />
                <br />
                <button className="btn text-muted text-center btn-success" type="button">Recover Password</button>
            </form>
        </div>
    </div>
    <div className="text-center">
        <ul className="list-inline">
            <li><a className="text-muted" href="#login" data-toggle="tab">Login</a></li>
            <li><a className="text-muted" href="#forgot" data-toggle="tab">Forgot Password</a></li>
        </ul>
    </div>

            </div>
        );
    }
}

export default login;
